import React, { useState } from 'react';

export const Contact = () => {

    const [vals, setVals] = useState({
            fio: "",
            address: "",
            email: "",
            phone: "",
            comment: ""
    });

    const handleChange = (e) => {
        setVals(oldVals => ({
            ...oldVals, 
            [e.target.name]: e.target.value
        }));
    }

    const handleSubmit = (e) => {
        console.log('ФИО: ' + e.target.fio.value)
        e.preventDefault();
    }

    return (
    <div className="form">
        <h2>Contact us</h2>
        <form onSubmit={handleSubmit}>
            <label htmlFor="fullName">
                <input
                value={vals.fio}
                type="text"
                name="fio"
                id="fullName"
                onChange={handleChange}
                placeholder="ФИО"
                required 
                />
            </label>

            <label htmlFor="address">
                <input
                value={vals.address}
                type="text" 
                id="address"
                name="address"
                onChange={handleChange}
                placeholder="Адрес"
                />
            </label>

            <label htmlFor="email">
                <input 
                value={vals.email}
                type="email" 
                id="email"
                name="email"
                onChange={handleChange}
                placeholder="Email"
                />
            </label>

            <label htmlFor="phone">
                <input 
                value={vals.phone}
                type="tel" 
                id="phone"
                name="phone"
                onChange={handleChange}
                placeholder="+79136779431"
                pattern="+7[0-9]{10}"
                required
                />
            </label>

            <label htmlFor="comment">
                <input 
                value={vals.comment}
                type="text" 
                id="comment"
                name="comment"
                onChange={handleChange}
                placeholder="Комментарий"
                />
            </label>

            <button type="submit">Submit</button>
        </form>
    </div>
)};
